    private String extractWeatherCondition(String weatherInfo){
        String weatherCondition;
        weatherCondition = weatherInfo.replaceAll(".*RealFeel® -?\\d+° (.*)","$1");
        weatherCondition = weatherCondition.replaceAll("(.*) Alte.*","$1");

        return weatherCondition;
    }

    private String extractWeatherTemperature(String weatherInfo){
        String temperature;
        temperature = weatherInfo.replaceAll(".*\\d{1,2}:\\d{1,2} (-?\\d+°)C.*","$1");
        return temperature;
    }



    @Test
    void weatherCondition1ShouldReturnNoros(){
        assertEquals("Noros",extractWeatherCondition(weatherCondition1));
    }

    @Test
    void weatherCondition2ShouldReturnPredominantnoros(){
        assertEquals("Predominant noros",extractWeatherCondition(weatherCondition2));
    }

    @Test
    void weatherTemperature1ShouldReturn32Degrees(){
        assertEquals("32°",extractWeatherTemperature(weatherTemperature1));
    }

    @Test
    void weatherTemperature2ShouldReturnMinus3Degrees(){
        assertEquals("-3°",extractWeatherTemperature(weatherTemperature2));
    }

    @Test
    void weatherTemperature3ShouldReturnMinus43Degrees(){
        assertEquals("-43°",extractWeatherTemperature(weatherTemperature3));
    }
  
